//Admin Only
<template>
  <div>
    <h2>Product Summary by Category</h2>
    <div v-for="item in summary" :key="item._id">
      <h4>Category: {{ item._id }}</h4>
      <ul>
        <li>Total Products: {{ item.totalProducts }}</li>
        <li>Avg Price: ₹{{ item.avgPrice.toFixed(2) }}</li>
        <li>Min Price: ₹{{ item.minPrice }}</li>
        <li>Max Price: ₹{{ item.maxPrice }}</li>
      </ul>
    </div>
  </div>
</template>

<script>
import axios from 'axios'
export default {
  data() {
    return {
      summary: [],
      role: localStorage.getItem('role')
    }
  },
  created() {
    if (this.role === 'Admin') {
      this.getSummary()
    } else {
      this.$router.push('/products')
    }
  },
  methods: {
    async getSummary() {
      const res = await axios.get('/summary')
      this.summary = res.data
    }
  }
}
</script>
